mapper.update:
UPDATE module_option SET
  option_name = #{name},
  option_value = #{value},
  option_title = #{title},
  option_order = #{order}
WHERE module_id = #{moduleId}
AND option_name = #{optionName}

mapper.delete:
DELETE FROM module_option
WHERE module_id = #{moduleId}
AND option_name = #{optionName}
